"use strict";
// Function
// Function Invocation means calling Function
// To execute a function, you simply invoke it by using its name
// followed by parentheses.
// Function of a company that recently hired
// new people and welcoming them
//Function Dfine
function greet(name, id) {
    console.log(`Welcome, ${name} and your id is ${id}`);
}
//Function Invocation (Call)
greet("musfira", 1);
function muusfira(name, id) {
    console.log(`Welcome, ${name} and your id is ${id}`);
}
muusfira("amna", 2);
function calculateAverage(numbers) {
    return numbers.reduce((acc, num) => acc + num, 0) / numbers.length;
}
